		/**
		 * @param {String} where
		 */
		private function setSize($where) {
			$this->setWhere($where);
			
			$result = $this->connection->execute(
					"SELECT count(1) AS \"telas.size\" from telas" . $this->getWhere());

			while ($row = $result->fetch_assoc()) {		
				$this->setResponse(0, "telas.size", $row["telas.size"]);
				
				$pages = ceil($row["telas.size"] / $this->connection->getItensPerPage());
				
				$this->setResponse(0, "telas.page", $this->connection->getPosition());
				$this->setResponse(0, "telas.pages", $pages);
				
				$pagination = "<select id='gz-select-pagination' onchange='goPage();'>";
				
				for ($i = 1; $i <= $pages; $i++) {
					if ($i == $this->connection->getPosition())
						$pagination .= "<option value='" . $i . "' selected>" . $i . "</option>";
					else
						$pagination .= "<option value='" . $i . "'>" . $i . "</option>";
				}	

				$pagination .= "</select>";
						
				$this->setResponse(0, "telas.pagination", $pagination);
			}

			$this->connection->free($result);
		}
		
