			$line = 0;

			$this->setRead($where, $order);
			$result = $this->connection->execute($this->getRead());

			while ($row = $result->fetch_assoc()) {
				$this->setResponse($line, "telas.id", $row["telas.id"]);
				$this->setResponse($line, "telas.tela", $row["telas.tela"]);
				$this->setResponse($line, "telas.tela.format", modelDoubleQuotes($row["telas.tela"]));
				$this->setResponse($line, "telas.tela.view", addLine($row["telas.tela"]));
